import React, { Component } from "react";

export default class Schule extends Component {
  constructor() {
    super();
    this.state = {
      title: "",
      description: ""
    };
  }

  changeState(e) {
    this.setState({});
  }
  render() {
    return (
      <div className="card">
        <div className="card-body">
          <h5 className="card-title text-center">Notes</h5>
          <div className="form-group">
            <input
              type="text"
              class="form-control mb-2"
              id="title-input"
              placeholder="Add title"
              name="title"
              onChange={this.changeState}
            />
            <textarea
              id="notes-text"
              className="form-control rounded-0"
              rows="10"
              placeholder="Add description"
              name="description"
            />
          </div>
          <button
            className="btn btn-primary m-1 font-weight-bold"
            onClick={gettext}
          >
            Add
          </button>
          <button className="btn btn-success font-weight-bold" onClick={erase}>
            Clean
          </button>
        </div>
      </div>
    );
  }
}

var erase = () => {
  document.getElementById("notes-text").value = " ";
};

var gettext = () => {
  var text = document.getElementById("notes-text").value;
};
